package graphics;import java.awt.image.BufferedImage;public class SpriteSheet {    private BufferedImage sheet;    private int spritCount;    private int scale;    private int spritesInWidth;    public SpriteSheet(BufferedImage sheet, int spritCount, int scale){        this.sheet = sheet;        this.spritCount = spritCount;        this.scale = scale;        this.spritesInWidth = sheet.getWidth()/scale;    }    public BufferedImage getSprite(int index) {        index = index % spritCount;        int x = index % spritesInWidth * scale;        int y = index / spritesInWidth * scale;        return sheet.getSubimage(x,y,scale,scale);    }}